mapper.select:
SELECT id, student_id, course_name, start_date, finish_date
FROM students_courses
WHERE student_id = #{studentId}

mapper.update:
UPDATE students
SET name=#{name}, furigana=#{furigana}, nickname=#{nickName}, e_mail=#{email}, region=#{region},
age=#{age}, gender=#{gender}, remark=#{remark}
WHERE student_id=#{studentId}

UPDATE students_courses
SET course_name=#{courseName}, start_date=#{startDate}, finish_date=#{finishDate}
WHERE id=#{id}
